// once login, the frontend will store this token in either LocalStorage, SessionStorage, or Cookies so that
// they can access restricted endpoints. 
const verifyToken = (req, res, next) => {
    const token = req.headers['authorization'];
    if (!token) {
        return res.status(403).json({ error: 'No token provided. Access denied.' });
    }

    jwt.verify(token, SECRET_KEY, (err, decoded) => {
        if (err) {
            return res.status(500).json({ error: 'Failed to authenticate token. Access denied.' });
        }
        // Save the decoded token for use in other routes
        req.profile_id = decoded;
        next();
    });
}

// Middleware to parse JSON bodies
app.use(express.json());

// GET request for user profile
// IN: username, token
// OUT: profile_id, username, password, email
app.get('/profile', verifyToken, (req, res) => {
    const { profile_id } = req.profile_id; // get the profile_id from the token
    // Find the user profile by profile_id
    const userProfile = profiles.find(profile => profile.profile_id === profile_id);
    if (!userProfile) {
        return res.status(404).json({ error: 'User not found' });
    }
    // Return the user profile information
    res.json({
        profile_id: profile_id,
        username: userProfile.username,
        password: userProfile.password, // May exclude the password for security reasons!
        email: userProfile.email
    });
});

// PUT request for changing profile info
// IN: newUsername, newEmail, token
// OUT: status message, profile obj, and new token
app.put('/profile', verifyToken, (req, res) => {
    const { profile_id } = req.profile_id; // get the username from the token
    const { newUsername, newEmail } = req.body;

    // Find the user profile by profile_id
    const userProfile = profiles.find(profile => profile.profile_id === profile_id);
    if (!userProfile) {
        return res.status(404).json({ error: 'User not found' });
    }
    
    // Check if the new username already exists
    if (profiles.some(profile => profile.username === newUsername && profile.username !== userProfile.username)) {
        return res.status(400).json({ error: 'Username already exists' });
    }

    // TO-DO Add other new username checks here.
    
    // Update the user profile information
    if (newUsername) userProfile.username = newUsername;
    if (newEmail) userProfile.email = newEmail;

    // Create a new token with the updated username
    const newToken = jwt.sign({ profile_id: userProfile.profile_id }, SECRET_KEY);
    
    res.json({ message: 'Profile updated successfully', profile: userProfile, token: newToken});
});

// POST request for registering a new user
// OUT: json message error or success.
// IN: username, password, and optional email
// SN: creates a Json Webtoken for authorization based on user_id which is a random UUID.
app.post('/register', async (req,res) => {
    try {
        const { username, password, email = ''} = req.body;

        if (!username || !password) {
            return res.status(400).json({ error: 'Please enter a valid username and password.'})
        }

        // Check if the username already exists
        const existingUser = profiles.find(profile => profile.username === username);
        if (existingUser) {
            return res.status(400).json({ error: 'Username already exists' });
        }

        // Encrypt their password and user_id
        const hashedPassword = await bcrypt.hash(password, 10);
        const user_id = 'user_' + crypto.randomUUID();

        // Add to database
        // TO-DO: implement MongoDB integration
        profiles.push({
            profile_id: user_id,
            username: username,
            password: hashedPassword,
            email: email
        })
        res.status(201).json({ message: 'User registered successfully' });
    } catch (error) {
        res.status(500).json({ error: 'Registration failed' });
    }
});

// POST request for logging in
// OUT: token or error message
// IN: username and password
app.post('/login', async (req, res) => {
    try {
        const { username, password } = req.body;

        // find user in database if they exist
        const user = profiles.find(profile => profile.username === username);
        if (!user) {
            return res.status(404).json({ error: 'User not found' });
        }
        // Compare the password
        const passwordMatch = await bcrypt.compare(password, user.password);
        if (!passwordMatch) {
            return res.status(401).json({ error: 'Invalid password' })
        }

        // Creat and send JWT token for authentication
        // secret key now depends on the user id which adds more protection if 
        // someone knows your username
        const token = jwt.sign({ profile_id: user.profile_id }, SECRET_KEY);
        res.status(200).json({ token });
    } catch (error) {
        res.status(500).json({ error: 'Login failed' });
    }
});

// POST request for deleting a profile
// OUT: json message of whether or not profile deleted successfully
// IN: profile_id and password
app.post("/profiledelete", verifyToken, async (req, res) => {
    try {
        const { profile_id } = req.profile_id;
        const { password } = req.body;
        
        // Find the user by profile_id
        const existingUser = profiles.find(profile => profile.profile_id === profile_id);
        if (!existingUser) {
            return res.status(400).json({ error: `No user to delete under ${username}` });
        }

        // Verify the password they enter to match the hashed one.
        const passwordMatch = await bcrypt.compare(password, existingUser.password);
        if (!passwordMatch) {
            return res.status(400).json({ error: 'Password does not match' });
        }

        // Remove the user from the profiles array
        const existingUserIndex = profiles.findIndex(profile => profile.profile_id === profile_id);
        profiles.splice(existingUserIndex, 1);
    
        res.status(201).json({ message: 'User deleted successfully' });
    } catch (error) {
        res.status(500).json({ error: 'Profile deletion failed' });
    }
});

// POST request for deleting a bubl
// OUT: success or error json message
// IN: bubl_id, user_id, token
app.post("/bubldelete", verifyToken, async (req, res) => {
    try { 
        const { bubl_id } = req.body;
        const { profile_id } = req.profile_id;

        // find bubl from bubl_id
        const bubl = bubls.find(bubl => bubl.bubl_id === bubl_id);
        if (!bubl) {
            return res.status(404).json({ error: `No bubl to delete` });
        }

        // check for existing user
        const existingUser = profiles.find(profile => profile.profile_id === profile_id);
        if (!existingUser) {
            return res.status(404).json({ error: `No user under ${username}` });
        }

        // check user's id is the same as bubl's creator.
        if (existingUser.profile_id !== bubl.creator_id) {
            return res.status(400).json({ error: 'You are not the owner of this bubl, you cannot delete it.' });
        }
        
        // otherwise, they are the owner. delete the bubl.
        const user_ind = profiles.findIndex(profile => profile.profile_id === profile_id);
        bubls.splice(user_ind, 1);

        res.status(201).json({ message: 'Bubl deleted successfully' });
    } catch (error) {
        res.status(500).json({ error: 'Bubl deletion failed' });
    }
});

// POST request for new bubl creation
// OUT: success or error json message
// IN: name of bubl, creator's id, optional members list, optional admins list, start date, and end date.
app.post("/bublcreate", verifyToken, async (req, res) => {
    try {
        // creation of a bubl goes here.
        const { profile_id } = req.profile_id;
        const { name, members = [], admins = [profile_id], start_date, end_date } = req.body;

        // validate required fields.
        if (!name || !profile_id || !end_date) {
            return res.status(400).json({ error: "Missing required fields. Please send atleast name of bubble, and the end date."});
        }

        // check that the cretor id exists in profiles.
        const creatorExists = profiles.some(profile => profile.profile_id === profile_id);
        if (!creatorExists) {
            return res.status(404).json({ error: "User does not exist in database." });
        }

        // Create new bubl's id by concatening with UUID
        const new_bub_id = name + "_" + crypto.randomUUID();

        const newBubl = {
            bubl_id: new_bub_id,
            name,
            creator_id: profile_id,
            members,
            admins,
            start_date: new Date(start_date),
            end_date: new Date(end_date)
        }
        // Add to database of bubls
        bubls.push(newBubl);

        res.status(201).json({ message: "Bubl created successfully", bubl: newBubl });
    } catch (error) {
        res.status(500).json({ error: "Bubl creation failed"});
    }
})

