// POST request for uploading a photo
// OUT: success or error msg, photo added to database.
// IN: photo name, profile_id of uploader, bubl_id for photo, and SVG data
function encrypt(text) {
    let iv = crypto.randomBytes(IV_LENGTH);
    let cipher = crypto.createCipheriv('aes-256-cbc', ENCRYPTION_KEY, iv);
    let encrypted = cipher.update(text);
    encrypted = Buffer.concat([encrypted, cipher.final()]);
    return iv.toString('hex') + ':' + encrypted.toString('hex');
}

function decrypt(text) {
    try {
        let textParts = text.split(':');
        let iv = Buffer.from(textParts.shift(), 'hex');
        let encryptedText = Buffer.from(textParts.join(':'), 'hex');
        let decipher = crypto.createDecipheriv('aes-256-cbc', ENCRYPTION_KEY, iv);
        let decrypted = decipher.update(encryptedText);
        decrypted = Buffer.concat([decrypted, decipher.final()]);
        return decrypted.toString();
    } catch (error) {
        console.error('Error decrypting data:', error);
        return ''; // Return empty string or handle the error appropriately
    } 
}

app.post("/photoupload", verifyToken, upload.single('photo'), async (req, res) => {
    try {
        const { photoname, bubl_id } = req.body;
        const { profile_id } = req.profile_id;

        const bubl = bubls.find((bubl) => bubl.bubl_id === bubl_id);
        if (!bubl) {
            return res.status(404).json({ error: "Bubl doesn't exist." });
        }

        const user = profiles.find((profile) => profile.profile_id === profile_id);
        if (!user) {
            return res.status(404).json({ error: "User doesn't exist." });
        }
        
        if (!req.file) {
            return res.status(400).send("No file uploaded.");
        }

        // Check if the uploaded file is a photo
        const allowedMimeTypes = ['image/jpeg', 'image/png', 'image/gif'];
        if (!allowedMimeTypes.includes(req.file.mimetype)) {
            return res.status(400).send("Uploaded file is not a valid photo.");
        }

        // Generate a unique ID for the photo
        const picture_id = 'picture_' + crypto.randomUUID();
        
        const base64Photo = req.file.buffer.toString('base64');
        
        const encryptedPhoto = encrypt(base64Photo);

        // Create a photo object
        const data = {
            bytes: encryptedPhoto,
            mimeType: req.file.mimetype,
            filename: req.file.originalname
        };
        const newPhoto = {
            picture_id,
            photoname,
            creator_id: profile_id,
            likes: [],
            bubl_id,
            data: data 
        };

        // Store the photo object in the array
        pictures.push(newPhoto);

        res.status(200).json({ message: 'Photo uploaded successfully!' });
    } catch (error) {
        res.status(500).json({ error: "Error uploading photo." });
    }
});


// GET request for getting a bubl's photos
// OUT: the photos of the bubl
// IN: bubl_id, token
app.post('/bublphotos', verifyToken, async (req,res) => {
    try {
        const { bubl_id } = req.body;
        const { profile_id } = req.profile_id;

        // console.log(bubl_id);
        // console.log(profile_id);

        if (!bubl_id || !profile_id) {
            return res.status(404).json("Error");
        }

        const bubl = bubls.find((bubl) => bubl.bubl_id === bubl_id);
        if (!bubl) {
            return res.status(404).json("Error");
        }

        const profile = profiles.find((profile) => profile.profile_id === profile_id);
        if (!profile) {
            return res.status(404).json("Error");
        }

        const inMembers = bubl.members.includes(profile_id);
        const inAdmins = bubl.admins.includes(profile_id);
        if (!inMembers && !inAdmins) {
            return res.status(400).json("Error")
        }

        returnArr = [];
        

        pictures.forEach((picture) => {
            const creator_user = profiles.find(profile => profile.profile_id === picture.creator_id);

            if (!creator_user) {
                return res.status(404).json("Error finding creator user");
            }

            if (picture.bubl_id === bubl_id) {
                const decryptedBytes = decrypt(picture.data.bytes);
                const decryptedPicture = {
                    ...picture,
                    creator_username: creator_user.username,
                    data: {
                        ...picture.data,
                        bytes: decryptedBytes
                    }
                };
                returnArr.push(decryptedPicture)
            }
        })

        return res.status(200).json(returnArr);
    } catch (error) {
        return res.status(500).json({ error: "Failed to retrieve bubl photos." });
    }
}); 
    
// Endpoint to like an image
app.post('/like/:id', verifyToken, (req, res) => {
    const photoId = req.params.id;
    const { profile_id } = req.profile_id; // Assuming profile_id is used to identify users
    // Find the photo by id
    const photo = pictures.find(photo => photo.picture_id === photoId);
  
    if (!photo) {
      return res.status(404).json({ error: 'Photo not found' });
    }
  
    // Check if the user has already liked the photo
    if (photo.likes.includes(profile_id)) {
      return res.status(400).json({ error: 'You have already liked this photo' });
    }
  
    // Add the user's id to the likes array
    photo.likes.push(profile_id);
    return res.status(200).json({ message: 'Photo liked successfully', likes: photo.likes.length });
  });
  

// Endpoint to unlike an image
app.post('/unlike/:id', verifyToken, (req, res) => {
    const photoId = req.params.id;
    const { profile_id }= req.profile_id; // Assuming profile_id is used to identify users
  
    // Find the photo by id
    const photo = pictures.find(photo => photo.picture_id === photoId);
  
    if (!photo) {
      return res.status(404).json({ error: 'Photo not found' });
    }
  
    // Check if the user has liked the photo
    const index = photo.likes.indexOf(profile_id);
    if (index === -1) {
      return res.status(400).json({ error: 'You have not liked this photo' });
    }
  
    // Remove the user's id from the likes array
    photo.likes.splice(index, 1);
  
    return res.status(200).json({ message: 'Photo unliked successfully', likes: photo.likes.length });
  });
  
